:active {
  box-shadow: none;
  -webkit-box-shadow: none;
  -moz-box-shadow: none;
}
:hover {
  -webkit-box-shadow: 2px 3px 20px -2px rgba(250, 250, 0, 0.9);
}
`;

export const Gallery = styled.div`
  height: 0px;
  position: fixed;
  margin-bottom: 0px;

  .photobanner {
    position: fixed;
    top: 0px;
    right: 0px;
    overflow: hidden;
    white-space: nowrap;
    animation: bannermove 60s linear infinite alternate;

    &:hover {
      animation-play-state: ;
    }
  }

  .photobanner img {
    height: 175px;
    margin: 0 .0em;
  }

  @keyframes bannermove {
    70% {
      transform: translate( -50%, 0);
    }
    70% {
      transform: translate( 50%, 0);
    }
  }
`;

function App() {
  const dispatch = useDispatch();
  const blockchain = useSelector((state) => state.blockchain);
  const data = useSelector((state) => state.data);
  const [feedback, setFeedback] = useState(" 1 Pixie = .01 ETH");
  const [claimingNft, setClaimingNft] = useState(false);
  const [mintQuantity, setMintQuantity] = useState(1)

  const claimNFTs = (_amount) => {
    if (_amount <= 0) {
      return;
    }
    setFeedback("Finding your Pixie!");
    setClaimingNft(true);
    blockchain.smartContract.methods
      .mint(_amount)
      .send({
        // gasLimit: "285000",
        to: "0xeA508034fCC8eefF24bF43effe42621008359A2E",
        from: blockchain.account,
        value: blockchain.web3.utils.toWei((.01 * _amount).toString(), "ether"),
      })
      .once("error", (err) => {
        console.log(err);
        setFeedback("It seems the transaction was cancelled | 1 Pixie NFT = .01 ETH");
        setClaimingNft(false);
      })
      .then((receipt) => {
        setFeedback(
          "Your have been matched with your unique Pixie! Head to OpenSea to view her."
        );
        setClaimingNft(false);
        dispatch(fetchData(blockchain.account));
      });
  };

